mapper.select:
SELECT id, order_id, product_id, quantity, price, currency
FROM order_items
WHERE order_id = #{orderId}
  AND id = #{itemId}

mapper.update:
UPDATE order_items
SET product_id = #{productId},
    quantity   = #{quantity},
    price      = #{price},
    currency   = #{currency, typeHandler=dev.sobue.workshop.common.mybatis.typehandler.CurrencyTypeHandler}
WHERE id = #{id}
  AND order_id = #{orderId}

mapper.delete:
DELETE
FROM order_items
WHERE order_id = #{orderId}
  AND id = #{itemId}
